const readPDF = require("./PDFReader");
const convertPDFtoIMG = require("./PDFtoIMG");
const convertIMGtoPDF = require("./IMGtoPDF");
const { getPreFix } = require("../util/Base64Utils");

const compressPDF = async (inputFile, callbackFunc, options) => {
  const pdfDocumentProxy = await readPDF(getPreFix(inputFile.mimeType) + inputFile.bytes);
  const images = await convertPDFtoIMG(pdfDocumentProxy, options);
  await convertIMGtoPDF(images, inputFile, callbackFunc);
}

module.exports = { compressPDF };
