module.exports.getFileName = (filename) =>
    filename.slice(0, filename.lastIndexOf(".")).toLowerCase();

module.exports.getImageObject = data => new Promise((resolve, reject) => {
    const image = new Image();
    image.src = data;
    image.onload = () => resolve(image);
    image.onerror = () => reject("Error cannot create the image");
});

module.exports.setCompressedFileFields = (inputFile, base64String) => {
    const base64PreFixLength = getPreFix(inputFile.mimeType).length;

    inputFile.bytes = base64String.substring(base64PreFixLength);
    inputFile.fileSize = getFileSize(base64String);
}

module.exports.createCanvas = (width, height) => {
    const { createCanvas } = require("canvas");
    const canvas = createCanvas(width, height);
    return canvas;
}

module.exports.createCanvasFromViewport = (viewPort) => {
    return createCanvas(viewPort.width || viewPort.viewBox[2], viewPort.height || viewPort.viewBox[3]);
}

module.exports.createCanvasContext = (canvas) => {
    return canvas.getContext('2d');
}

module.exports.getFileExtension = (filename) =>
    filename.slice(filename.lastIndexOf(".") + 1).toLowerCase();
